import React, {useState} from 'react'
import {useHttp} from "../hooks/http.hook";
export  const AuthPage = () => {
    const { loading,error,request} = useHttp()
    const [form,setForm] = useState( {
        email:'',password:''
    })
    useEffect (()=> {
      }, [error])

const changeHandler = event => {
        setForm({...form, [ event.target.name]: event.target.value})

    console.log(form)}
    const registerHandler = async ()=>{
        try {
            const data = await request('/api/auth/register', 'POST', {...form})

            console.log('Data', data)
        }   catch (e) { }
    }

        return (<div className="row">
        <div className="col s6 offset-s3">
        <h1> Ssulka blaha </h1>
            <div className="card blue darken-1">
                <div className="card-content white-text">
                    <span className="card-title">avtoruzacuya</span>
                    <div className="input-field ">
                        <input placeholder="vvedite imail"
                               id="email"
                               type="text"
                               name="email"
                                className="yellow-input"
                               onChange = {changeHandler}
                        />

                            <label htmlFor="email">Email</label>
                    </div>
                    <div className="input-field ">
                        <input placeholder="vvedute parol "
                               id="password"
                               type="password"
                               name="password"
                               onChange = {changeHandler}
                        />
                        <label htmlFor="password">Paroll</label>
                    </div>
                </div>
                <div className="card-action">
            <button className="btn yellow  darken-4" style={{marginRight:10}}> Voytu</button>
                    <button className="btn grey lighten-1 black-text"
                            onClick= {registerHandler}  >
                        registracuya
                    </button>
                </div>
            </div>
    </div>
        </div>)

}
